import React, {useState} from 'react'
import {Button, Modal, Form, InputGroup, FormControl} from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import { HideModalNewToDo, ShowModalNewToDo } from '../../actions/Modal';


export const ModalNewToDo = () => {

    const dispatch = useDispatch()
    const { showModal } = useSelector( state => state.modal )
    const [formValues, setformValues] = useState({
        Name: '',
        Description: '',
        LimitDate: '',
        Criticality: ''
    })

    const handleClose = () => dispatch( HideModalNewToDo() );

    const handleChange = ({ target }) => {
        setformValues({
            ...formValues,
            [target.name]: target.value
        });
    }


    const handleSubmit = () => {
        if( isValid() ){

            console.log( formValues )
        }
    }


    const isValid = () => {
        return true;
    }




    return (
    <>
        <Modal show={showModal} onHide={handleClose}>
        <Modal.Header closeButton>
            <Modal.Title>Nuevo ToDo!!!</Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <Form onChange={ handleChange }>

                <Form.Group className="mb-3 has-error" controlId="formBasicEmail">
                    <Form.Label>Nombre</Form.Label>
                    <Form.Control type="text" name='Name' value={ formValues.name } placeholder="Nombre" />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Label>Descripcion</Form.Label>
                    <Form.Control type="text"  name='Description' value={ formValues.Description } placeholder="Descripcion" />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Label>Fecha limite</Form.Label>
                    <Form.Control type="date" name='LimitDate' value={ formValues.LimitDate } placeholder="Fecha limite" />
                </Form.Group>


                <Form.Group className="mb-3 mt-3"> 
                    <Form.Label style={{ display: 'block' }}>Criticidad</Form.Label>

                    <Form.Check
                        inline
                        label="Baja"
                        name="Criticality"
                        value='0'
                        type='radio'
                        id={`inline-radio-1`}
                    />
                    <Form.Check
                        inline
                        label="Media"
                        name="Criticality"
                        value='1'
                        type='radio'
                        id={`inline-radio-2`}
                    />
                    <Form.Check
                        inline
                        label="Alta"
                        name="Criticality"
                        value='2'
                        type='radio'
                        id={`inline-radio-3`}
                    />
                </Form.Group>
            </Form>
        </Modal.Body>
        <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
            Cancelar
            </Button>
            <Button variant="primary" onClick={ handleSubmit }>
            Confirmar
            </Button>
        </Modal.Footer>
        </Modal>
    </>
    );
}
