import React,{ useState } from 'react';
import { Card, Form, Button } from 'react-bootstrap';




export const AuthPage = () => {

    const [loginForm, setLoginForm] = useState(true);




    return (
        <div className="d-flex align-items-center justify-content-center" style={{ height: '100vh'}}>
            <Card style={{ width: '24rem', height: 'auto' }}>
                <Card.Body>
                    { 
                        loginForm ?
                        <LoginForm setLoginForm={setLoginForm}/> :
                        <RegisterForm setLoginForm={setLoginForm}/>

                    }
                </Card.Body>
            </Card>
        </div>
    )
}




const LoginForm = ({setLoginForm}) => {


    const [formValues, setformValues] = useState({
        email: '',
        password: ''
    })

    const handleChange = (e) => {
        setformValues({
            ...formValues,
            [e.target]: [e.target.value]
        })
    }




    return (
        <Form onChange={handleChange}>
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Email</Form.Label>
                <Form.Control type="email" placeholder="Email" value={ formValues.email }/>
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicPassword">
                <Form.Label>Contraseña</Form.Label>
                <Form.Control type="password" placeholder="Contraseña" value={ formValues.password }/>
            </Form.Group>


            <Button variant="primary" type="submit" size='lg'>
                Ingresar
            </Button>


            <p style={{ marginTop: '1rem'}}>
                No tenes cuenta? Podes registrarte <a href='#' onClick={ () => setLoginForm(false) }>aqui</a>
            </p>
        </Form>
    )



}


const RegisterForm = ({setLoginForm}) => {


    return (
        <Form>
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Email</Form.Label>
                <Form.Control type="email" placeholder="Email" />
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Nombre de usuario</Form.Label>
                <Form.Control type="email" placeholder="Nombre de usuario" />
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicPassword">
                <Form.Label>Contraseña</Form.Label>
                <Form.Control type="password" placeholder="Contraseña" />
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicPassword">
                <Form.Label>Repita la contraseña</Form.Label>
                <Form.Control type="password" placeholder="Repita la contraseña" />
            </Form.Group>


            <Button variant="primary" type="submit" size='lg'>
                Ingresar
            </Button>


            <p style={{ marginTop: '1rem'}}>
                Ya tenes cuenta? Podes ingresar <a href='#' onClick={ () => setLoginForm(true) }>aqui</a>
            </p>
        </Form>
    )


}
