import React, { useEffect } from 'react'
import { Row, Col, Container, Card, Buttom, Badge } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import { StartObtenerTodos } from '../../actions/Todos'

export const HomePage = () => {

    const dispatch = useDispatch()
    const { user, todos } = useSelector(state => state)


    useEffect(() => {
        dispatch( StartObtenerTodos( user.token ))
    }, [])

    console.log(todos)






    return (
        <Row style={{ height: '90vh' }} class='d-flex justify-content-center aling-items-center'>
            <Col style={ styles.col } className='d-flex justify-content-center'>
                <Container>

                    <div style={{ textAlign: 'center', margin: '1rem', borderBottom: '1px solid black' }}>
                        <h4>Pendientes</h4>
                    </div>

                    <Row>


                        {
                            todos.toDos.map( item => (
                                <ToDoItem todo={ item }/>
                            ))
                        }
                    </Row>

                </Container>
            </Col>
        </Row>  
    )
}



const ToDoItem = ({ todo }) => {


    console.log(todo)


    const getBg = () => {
        console.log(todo.Criticality);
        switch( todo.Criticality ){
            case 'HIGH': 
                return 'red'
            case 'MEDIUM' : 
                return 'yellow'
            default : 
                return 'white'
        }
    }


    return (
        <Card style={{ width: '100%', margin: '1rem 0' }} key={ todo._id } bg={getBg()}>
            <Card.Body>
                <Card.Subtitle className="mb-2 text-muted">Card Subtitle</Card.Subtitle>
                <Card.Title>{ todo.Name }</Card.Title>
                <Card.Text>
                { todo.Description }
                </Card.Text>
                <Badge style={ styles.badge } className='m-2'>Eliminar</Badge>{"   "} 
                <Badge style={ styles.badge }>Listo</Badge>
            </Card.Body>
            <Card.Footer style={{ textAlign: 'right'}}>
                <Card.Text>Completada hace 4 dias</Card.Text>
            </Card.Footer>
        </Card>
    )


}



const styles = {
    col: {
        backgroundColor: 'white',
        borderRadius: '5px',
        minHeigth: '85vh',
        margin: '10px',
        overflowY: 'scroll',
        maxHeight: '100%'
    },
    badge: {
        cursor: 'pointer'
    }
}
